(5460, 742, '_designs_block_block-_crb_row_images_sources_0_link-_crb_image_link_2', 'madame.lefigaro.fr'),
(5461, 742, '_designs_block_block-_crb_row_images_sources_0_link-_crb_image_link_3', 'blog.urbanoutfitters.com'),
(5462, 742, '_designs_block_block-_crb_row_images_sources_0_link-_crb_image_link_4', 'camillapihl.no'),
(5463, 742, '_designs_block_block-_crb_subcontent_0', '<p style="text-align: center;">#colour #green #plants #freshness #livingroom #furniture #style #girl #cosiness #interior #design #architecture #spacefilling</p>\r\n<p style="text-align: left;">If your life lacks balance, the green colour in the interior, where you live and work, will help achieve harmony with yourself and the surrounding world. You will easier cope with negativity and tiredness in a green room. Add fragments of prints of plants to your walls, whether it is a realistic photograph or a drawing. This will break the monotony of walls and be a direct quotation of the nature. Aside from walls, such prints can come in handy in textile: pillows, blankets, and curtains.</p>'),
(5464, 742, '_designs_block_block-_crb_subtitle_1', ''),
(5465, 742, '_designs_block_block-_crb_row_images_1_image-_crb_d_image_0', '740'),
